import './words.css';

function Length4(props) {
	const length = props.simplified.length;
	const single = props.simplified != props.traditional;
	if (props.simplified != props.traditional) {
		return (
			<div class="text" id="word4">
				<p class={`simp simp-margin-${length} text-size-${length}`}>
					{props.simplified}
				</p>
				{single ? <p class={`comp comp-margin-${length} text-size-${length}`}>
					<strong>
						{props.traditional}
					</strong>
				</p> : <div/>
				}
				<div class={`width-${length}`}>
					<p class="pinyin">
						<strong>
							{props.pinyin}
						</strong>
					</p>
				</div>
				<div class={`trans trans-margin-${length}`}>
					<em>
						{props.trans}
					</em>
				</div>
			</div>
		)
	} else {
		return (
			<div class="text" id="word-single-4">
				<p class="simp simp-single-margin-4 text-size-single-4">
					{props.simplified}
				</p>
				<div class="width4">
					<p class="pinyin">
						<strong>{props.pinyin}</strong>
					</p>
				</div>
				<div class="trans trans-single-margin-4">
					<em>{props.trans}</em>
				</div>
			</div>
		);
	}
}

