// import logo from './logo.svg';
import './headline.css';

function SingleComponent(props) {
  return (
    <div className={`container common ${props.type}`}>
      {props.label}
    </div>
  );
}

const villains = [
  "What",
  "Xi Jinping",
  "CCP leadership",
  "Chen Weihua",
  "The PLA",
  "China",
  "Hong Kong Police"
]

function Headline(props) {
  var items = []
  const range= [...Array(villains.length).keys()]
  for (const [index, value] of range.entries()) {
      items.push(
        <div className="flexbox">
          <SingleComponent type="villain" label={villains[index % villains.length]}/>
          {/* <SingleComponent type="action" label={value}/>
          <SingleComponent type="victim" label={value}/>
          <SingleComponent type="reason" label={value}/> */}
        </div>
      );
  }
  return (
    <div>
      {items}
    </div>
  );
}

export default Headline;
