import './countdown.css';
import 'jquery-csv';

function Word(props) {
	const style = "countdown";
	const length = 4;
	const suffix = `${length}`;
	return (
		<div
            class={`container clear-space ${style}`} id="word"
            style={
                {
                    backgroundImage: "url(\"assets/" + props.idx + ".png\")"
                }
            }
            >
			<div class={`simp simp-margin-${suffix} text-size-${suffix}`}>
				{props.trans_top}
			</div>
            <div class="flexbox">
                <div>
                    {props.chinese}
                </div>
                <div>
                    {props.pinyin}
                </div>
            </div>
			<div class={`trans trans-margin-${suffix}`}>
				<em>
					{props.trans_bottom}
				</em>
			</div>
		</div>
	);
}

// ā á ǎ à ō ó ǒ ò è é è ě ī í ǐ ì ū ú ǔ ù ǖ ǘ ǚ  ǜ  ü

const countdown_words = [
	["阴性", "yīn xìng", "COVID-19", "The novel coronavirus spreads across the globe, impacting countless lives"],
];

function Countdown(props) {
	var all_words = countdown_words;
	var items = []
    const range20 = [...Array(5).keys()]
	for (const i of range20) {
        const idx = i+1
		const [simp, pinyin, trans_top, trans_bottom] = all_words[0]
		items.push(
			<Word
                idx={idx}
				chinese={simp}
                trans_bottom={trans_bottom}
				pinyin={pinyin}
				trans_top={trans_top}/>
		);
	}
	return (
    	<div>
      		{items}
    	</div>
  	);
}

export default Countdown;
