import './countdown.css';
import 'jquery-csv';

function Word(props) {
	const style = "countdown";
	const length = 4;
	const suffix = `${length}`;
	return (
		<div class={`container clear-space ${style}`} id="word">
			<p class={`simp simp-margin-${suffix} text-size-${suffix}`}>
				{props.trans_top}
			</p>
			<div class={`width-${suffix}`}>
				<p class="pinyin">
					<strong>
						{props.pinyin}
					</strong>
				</p>
			</div>
			<div class={`trans trans-margin-${suffix}`}>
				<em>
					{props.trans_bottom}
				</em>
			</div>
		</div>
	);
}

// ā á ǎ à ō ó ǒ ò è é è ě ī í ǐ ì ū ú ǔ ù ǖ ǘ ǚ  ǜ  ü

const countdown_words = [
	["阴性", "yīn xìng", "COVID-19", "The novel coronavirus spreads across the globe, impacting countless lives"],
];

function Countdown(props) {
	var all_words = countdown_words;
	var items = []
	for (const [index, value] of all_words.entries()) {
		const [simp, pinyin, trans_top, trans_bottom] = value
		items.push(
			<Word
				chinese={simp}
                trans_bottom={trans_bottom}
				pinyin={pinyin}
				trans_top={trans_top}/>
		);
	}
	return (
    	<div>
      		{items}
    	</div>
  	);
}

export default Countdown;
